public class Lab02_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte b1 = 60, b2 = 70;
		char c1 = 'A', c2 = 1;
		int bSum = b1 + b2;
		int cSum = c1 + c2;
		System.out.println("bSum (value): " + bSum);
		System.out.println("cSum (value): " + cSum);
		System.out.println("cSum (letter): " + (char)cSum);
	}

}
